mapper.select:
select smi.market_code as code, smi.market_name as name, smi.open_point as openPoint, smi.cur_point as curPoint, smi.pre_close_point as preClosePrice, smi.trade_amount as tradeAmt, smi.trade_volume as tradeVol, smi.cur_point-smi.pre_close_point as upDown, (smi.cur_point-smi.pre_close_point)/smi.pre_close_point as rose, (smi.max_point-smi.min_point)/smi.pre_close_point as amplitude, smi.cur_time as curTime from stock_market_index_info as smi where smi.market_code in
#{marketId}
and smi.cur_time=#{timePoint}

select
date_format(smi.cur_time,'%Y%m%d%H%i') as time,
sum(smi.trade_amount) as count
from stock_market_index_info as smi
where smi.market_code in

    #{marketId}

and smi.cur_time between #{startTime} and #{endTime}
group by smi.cur_time
order by time asc;

mapper.insert:
insert into stock_market_index_info
( id,market_code, market_name, pre_close_point, open_point, cur_point,
 min_point, max_point, trade_amount, trade_volume, cur_time
)
values

    (#{smi.id,jdbcType=BIGINT},#{smi.marketCode,jdbcType=CHAR}, #{smi.marketName,jdbcType=VARCHAR}
    ,#{smi.preClosePoint,jdbcType=DECIMAL},#{smi.openPoint,jdbcType=DECIMAL},#{smi.curPoint,jdbcType=DECIMAL}
    ,#{smi.minPoint,jdbcType=DECIMAL},#{smi.maxPoint,jdbcType=DECIMAL}
    ,#{smi.tradeAmount,jdbcType=BIGINT},#{smi.tradeVolume,jdbcType=DECIMAL}
    ,#{smi.curTime,jdbcType=TIMESTAMP}
    )
